        const dy = t.y - y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return distance < minSpacing;
      });

      if (!tooClose) {
        tables.push({
          id: `table-${Date.now()}-${i}`,
          x,
          y,
          shape,
          diameter,
          seats: 10,
          name: `Mesa ${i + 1}`,
          number: i + 1,
        });
        placed = true;
      }

      attempts++;
    }
  }

  return tables;
}

